mapper.insert:
INSERT INTO member (
user_id, pass, nickname, email, bio, location, admin
)VALUES(
#{user_id}
,#{pass}
,#{nickname}
,#{email}
,#{bio}
,#{location}
,false
)

mapper.select:
SELECT COUNT(user_id) FROM
member
WHERE user_id = #{param1}

SELECT COUNT(nickname) FROM
member
WHERE nickname = #{param1}

SELECT COUNT(*) FROM member
WHERE user_id = #{user_id}
  AND email = #{email}

mapper.update:
UPDATE member
SET pass = #{pass}
WHERE user_id = #{user_id}

UPDATE member
SET img_idx = #{img_idx}
WHERE user_id = #{user_id}
